// import logo from './logo.svg';
import './App.css';
// components na folder ma navbar 6
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';

// import About from './components/About';

import React,{ useState } from 'react';
function App() {
  const[mode,setMode]=useState('light');
  const[modename,setModename]=useState('enable Dark Mode')
  // const[textcolor,setTextcolor]=useState('text-light')
  const toggleMode=()=>{
    if (mode==='light') {
      setMode('dark')
      setModename('Enable Light Mode')
      document.body.style.backgroundColor="#042743"
      // setTextcolor('light')
    }
    else
    {
      setMode('light')
      setModename('Enable Dark Mode')
      document.body.style.backgroundColor="white"
      // setTextcolor('dark')

    }
  }
  return (
    <>
    {/* aa ma je title use karyo 6 ae props tarike use thay 6 */}
      {/* <Navbar title={3}></Navbar> */}
      <Navbar title="textutils" mode={mode} toggleMode={toggleMode}modename={modename} ></Navbar>
      {/* <Navbar ></Navbar> */}

      <div className="container my-3">

      <TextForm mode={mode} heading="Enter text to analyze"></TextForm>
      {/* <About></About> */}
      </div>
    </>
  );
}

export default App;
