import React,{useState} from 'react'

export default function TextForm(props) {
    let handleonclick=()=>{
        // console.log("click")
        // setText("you have click")
        let newtext=text.toUpperCase();
        setText(newtext)
    }
    let handlecoclick=()=>{
        // console.log("click")
        // setText("you have click")
        let newtext=text;
        setText(newtext)
    }
    let handleonchange=(event)=>{
        // console.log("onchange")
        // console.log(event.target.value)
        setText(event.target.value)
    }
    // const [text,setText]=useState('enter');
    const [text,setText]=useState('');
    return (
        <>
        <div className="container" style={{color:props.mode==='dark'?'white':'black'}}>

            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" value={text} onChange={handleonchange} style={{backgroundColor:props.mode==='dark'?'grey':'white',color:props.mode==='dark'?'white':'black'}}
                id="mybox" rows="8" placeholder='enter your text'></textarea>
                <button className="btn btn-primary mx-1" onClick={handleonclick}>convert to upper case</button>
                <button className="btn btn-primary mx-1" onClick={handlecoclick}>Copy text</button>
            </div>
            <div className="container">
                <h1>YOUR SUMMERY</h1>
                {/* count number of words:-split and character:length */}
                <p>{text.split(" ").length} words and {text.length} character</p>
                <p>{0.0008 * text.split(" ").length} minutes to read</p>
            </div>
        </div>
        </>
    )
}
