// import logo from './logo.svg';
import './App.css';
// components na folder ma navbar 6
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';

import About from './components/About';
import React,{ useState } from 'react';
function App() {
  const[mode,setMode]=useState('dark');
  const[modename,setModename]=useState('Light Mode')
  const[textcolor,setTextcolor]=useState('text-light')
  const toggleMode=()=>{
    if (mode==='light') {
      setMode('dark')
      setModename('Light Mode')
      setTextcolor('light')
    }
    else
    {
      setMode('light')
      setModename('Dark Mode')
      setTextcolor('dark')

    }
  }
  return (
    <>
    {/* aa ma je title use karyo 6 ae props tarike use thay 6 */}
      {/* <Navbar title={3}></Navbar> */}
      <Navbar title="textutils" mode={mode} toggleMode={toggleMode}modename={modename} textcolor={textcolor}></Navbar>
      {/* <Navbar ></Navbar> */}

      <div className="container my-3">

      <TextForm heading="Enter text to analyze"></TextForm>
      <About></About>
      </div>
    </>
  );
}

export default App;
